import React,{Component} from 'react'
import Axios from 'axios'

export default class ImageContainer extends Component{

    state={
        mediaImages:[]
    }

    componentDidMount(){
        axios.get(`https://tengezastudios.co.ke/wp-ckr/wp-json/wp/v2/media/${this.props.images.featured_media}`)
        .then(res =>{
            this.setState({
                mediaImages: res.data.media_details.sizes.full.source_url
            })
        })
        .catch(err => console.log(err))
    }
    render(){
        return(
            <React.Fragment>
                <div className="images-container">
                        {this.state.mediaImages.map((image,index) =>{
                            return(
                                <img src={image} key={index} className="images"/>
                            )
                        })
                        }
                    </div>
            </React.Fragment>
        )
    }
}
